function Footer() {
  const hour = new Date().getHours();
  const openHour = 12;
  const closeHour = 22;
  const isOpen = hour >= openHour && hour <= closeHour;

  return (
    <footer className="footer">
      <div className="order">
        {isOpen && (
          <p>
            We're open untill {closeHour}:00. Come visit us or order online.
          </p>
        )}
        <button className=""></button>
      </div>
    </footer>
  );
}

export default Footer;
