import pizzaData from "./../../public/";

function Pizza(props) {
  return (    
      props.pizzaData.map((el) => {
      return <div className="pizza" key={el.id}>
        <img src = {el.img} alt={el.name}></img>
      <div>
        <h3>{el.name}</h3>
        <p>{el.ingredients}</p>
        <span>{el.price}</span>
      </div>
      </div> 
    })     
  )
  }

function Menu() {
  return (
    <div className="menu">
      <h2>Our menu</h2>
      <Pizza pizzaData={pizzaData}/>
    </div>
  );
}

export default Menu;
